#include <fstream>#include "StrType.h"#include <iostream>const int MAX_WORDS = 10;int main(){  using namespace std;  StrType word;  ifstream inFile;  StrType words[MAX_WORDS];  int numWords = 0;  inFile.open("words.in");  word.MakeEmpty();  word.GetStringFile(true, ALPHA_NUM, inFile);  while (inFile && numWords < MAX_WORDS)  {    word.CopyString(words[numWords]);    numWords++;    word.GetStringFile(true, ALPHA_NUM, inFile);  }  if (inFile)    cout << "First " << MAX_WORDS << " words on the file: ";  else    cout << " Words on the file: ";  for (int index = 0; index < numWords; index++)    words[index].PrintToScreen(true);  return 0;}